export const reducer = (state, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    default:
      return state;
  }
};

export const watchReducer = (state, action) => {
  switch (action.type) {
    case "GETS_DATA_COMPLETED":
      return action.payload.filter((event) => event.completed === true);
    default:
      return state;
  }
};

export const basketReducer = (state = { total:0, items:[]}, action) => {
  switch (action.type) {
    case "ADD_ITEM":
      const item = [...state.items, action.payload]
      var totals = 0;
      item.forEach(el => {
        totals += el.quantity * el.price
      })
      return {
        items: item, 
        total: totals,
      }
    case "INCROMENT":

        return { ...state.items,items:[{quantity: action.payload + 1}]}

      case "DECROMENT":
        if (action.payload > 1) {
           return [...state.items,
           { quantity: action.payload - 1}
        ];
        } else {
         return {...state.items, quantity: action.payload}
        }
    default:
      return state;
  }
};




export const itemReducer = (state , action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      const data = action.payload
      return {
        ...data,
        quantity: 1
      };
    case "TOGGLE_WATCH":
      return {
        ...state,
        completed: !action.payload.completed,
      };
      case "TOGGLE_BASKET":
        return {
          ...state,
          basket: !action.payload.basket,
        };
    case "INCROMENT":
      return {
        ...state,
        quantity: state.quantity + 1
      }
    case "DECROMENT":
      if (state.quantity > 1) {
         return {...state,
          quantity: state.quantity - 1
            };
      } else {
       return {...state, quantity: state.quantity}
      }
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return state;
  }
};
