import { React, useEffect, useContext } from "react";
import WatchList from "./WatchList";
import { prodactsConext } from "../context/contextData";

const WatchApi = () => {
  const { dispatchWatch, watchItem, user } = useContext(prodactsConext);

  return (
    <div>
      <h1 className="text-center">Watch List</h1>
      {watchItem.length ? (
        <div className="container">
          <div className="row">
            {watchItem.map((el) => {
              return (
                <WatchList
                  key={el._id}
                  oneItem={el}
                  dispatchWatch={dispatchWatch}
                  user={user}
                />
              );
            })}
          </div>
        </div>
      ) : (
        <h3 className="text-center">Watch List is Empty</h3>
      )}
    </div>
  );
};

export default WatchApi;
