import { React, useContext, useEffect,useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from '../recent/RecentList';
import DescountsList from '../discounts/DiscountsList'
import Carousel from 'react-bootstrap/Carousel'
import axios from "axios";

function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);

// Creat Fack User if there is no real User
    if (!user.user) {
     const _id = Date.now()
      dispatch({ type: "FACK_USER", payload:{ _id:_id} });
     }

  useEffect(() => {

      getsReq().then((result) => {
        dispatch({ type: "FATCH_SUCCESS", payload: result.data})
    })
  }, []);


  return (
    <div className="container">
      <Carousel fade>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="/images/Apple/apple1.jpg"
      alt="First slide"
    />
    <Carousel.Caption>
      <h3>First slide label</h3>
      <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      rc="/images/samsung/01.jpg"
      alt="Second slide"
    />

    <Carousel.Caption>
      <h3>Second slide label</h3>
      <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
    </Carousel.Caption>
  </Carousel.Item>
  <Carousel.Item>
    <img
      className="d-block w-100"
      src="/images/svg/Untitled-1.jpg"
      alt="Third slide"
    />

    <Carousel.Caption>
      <h3>Third slide label</h3>
      <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
    </Carousel.Caption>
  </Carousel.Item>
</Carousel>

      <div className="row">


        {products.length ? products.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              dispatch={dispatch}
            />
          );
        }) : 'loading'}
        <RecentList />
        <DescountsList/>
      </div>
    </div>
  );
}

export default HomeApi;
