import { reducer,basketReducer } from "../reducers/reducer";

export const prodactsConext = createContext();

export const ProductsProvider = props => {
    const [data, dispatch] = useReducer(reducer, []);
    const [basket, dispatch1] = useReducer(basketReducer, {});
    return (
        <prodactsConext.Provider value={{data, dispatch, basket, dispatch1}}>
           {props.children}
        </prodactsConext.Provider >
   );
}
