import React, { useContext,useState } from "react";
import { Link } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import Search from "../search/Search";

const NavBar = () => {
  const { user, dispatch } = useContext(prodactsConext);
  const [open, setOpen] = useState(false)


const searchToggle = (e) => {
  setOpen(!open)
}



  return (

<nav>
    <div class="menu">
        <ul>
            <li><a href=""><i class="fa fa-apple"></i></a></li>
          <li><a href="#" className={ open ? "menu-item hide-item" :menu-item} >Mac</a></li>
            <li><a href="#" class="menu-item">iPad</a></li>
            <li><a href="#" class="menu-item">iPhone</a></li>
            <li><a href="#" class="menu-item">Watch</a></li>
            <li><a href="#" class="menu-item">TV</a></li>
            <li><a href="#" class="menu-item">Music</a></li>
            <li><a href="#" class="menu-item">Support</a></li>
            <li><a href="#" id="search" onClick={searchToggle}><i class="fa fa-search"></i></a></li>
            <li><a href="#"><i class="fa fa-shopping-basket"></i></a></li>
        </ul>
        <div class="search-form">
            <form>
            <input type="text" placeholder="Search"/>
            </form> 
        </div>
        <a class="close"><i class="fa fa-times"></i></a>
    </div>
</nav>



  );
};

export default NavBar;
