import React from 'react'
import { useTransition, animated, useSprings } from 'react-spring'

const SliderScreen = ({ el, i }) => {
  const fade = useSprings({
    from: {
      opacity: 0
    },
    to: {
      opacity: 1
    }
  });
    return (
      <div className="textCardS">
        <animated.div className={`slideHeader${i} slideHeader`} style={fade}>
          <h3>{el.title}</h3>
          <p>{el.par}</p>
        </animated.div>
        <img width="100%" src={el.img} alt=""/>
      </div>
    )
}

export default SliderScreen
