import React, { useState, useEffect,CSSProperties } from "react";
import "./item.css";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";



const Image = ({ product }) => {
  const [images, setImages] = React.useState([]);
const [count, setCount] = useState(0)
  useEffect(() => {
    setImages(
      product.img
        ? product.img.map((url) => ({
            original: `http://localhost:3001/${url.original}`,
            thumbnail: `http://localhost:3001/${url.original}`,
            originalClass: "featured-slide",
            thumbnailClass: "featured-thumb",
            description: "",
          }))
        : ""
    );
  }, [product]);
const colors = product.img && el.img.map(el => el)

console.log("images", colors, count)

  return (
    <>
      <div className="item" >
          {images ? <ImageGallery items={images} additionalClass="app-image-gallery " onSlide={(e => setCount(e))} /> : null}
        <div className="appShadowGallery" style={{ backgroundColor: colors[count].color.Muted }} />
        {edit ? (
          <Edit
            key={product._id}
            setEdit={setEdit}
            dispatchOneItem={dispatch}
          />
        ) : (
            <div className="col ">
              <Image el={product} />
            <h1>{product.title}</h1>

            <div className="cardS">
              <div className="containerI shadow">

              </div>
              <p>{product.description}</p>
            </div>
            <div className="card-body">
              <h5>{product.category}</h5>
              <div>{product.price} €</div>
              <div>Quantity: {product.quantity}</div>
            </div>
            {user.loggedIn &&
            user.user &&
            product.owner &&
            product.owner._id == user.user._id ? (
              <button
                onClick={() => setEdit(!edit)}
                className="btn btn-secondary"
              >
                {" "}
                update
              </button>
            ) : (
              <Buy key={product._id} card={[product]} />
            )}
            <button
              className="btn"
              onClick={() => dispatch({ type: "WATCH_ITEM", payload: product })}
            >
              {watch.some((el) => el._id === product._id)
                ? "Watched"
                : "add watch"}
            </button>
            <button
              className="btn"
              onClick={() => dispatch({ type: "ADD_ITEM", payload: product })}
            >
              add to cart
            </button>
            <button onClick={() => dispatch({ type: "DECROMENT_ITEM" })}>
              {" "}
              -{" "}
            </button>
            <button onClick={() => dispatch({ type: "INCROMENT_ITEM" })}>
              {" "}
              +{" "}
            </button>
          </div>
        )}
      </div>
    </>
  );
};

export default Image;
