import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

const SellerForm = () => {
  const [inputText, setInputText] = useState({
    title: "",
    type: "",
    price: "",
    img: []
  });

  const [addProduct, setAddProduct] = useState(false);
	const [Message	, setMessage] = useState([]);
  const [isData, setisData] = useState(false)
  const [img, setimage] = useState([])
  useEffect(() => {
    sendData();
  }, [addProduct]);


  const handleChange = (e) => {
    const value = e.target.value;
     setInputText({ ...inputText, [e.target.name]: value });

      [e.target.files].forEach(e => {
        let image = URL.createObjectURL(e)
        setimage(image);
      }
      );


    setisData(true)
  };

console.log('isData', isData)

  console.log("addProduct", addProduct);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isData === true) {
     setAddProduct(inputText,img);
      setInputText({
        title: "",
        type: "",
        price: "",
        imageURL: ""
      })
      setisData(false)
      setMessage({ text: 'The data has been sent successfully' })
    } else {
     setMessage({ text: 'add a prodact' })
    }
	 setTimeout(() => {
    setMessage([])
   }, 2000);
  };


	const sendData = () => {
    if (!addProduct === false) {

      const myURL = "http://localhost:3001/products";
      axios({
        method: "post",
        url: myURL,
        data: JSON.stringify(addProduct),
        headers: { "Content-Type": "application/json" },
      })
        .then(function (response) {
          //handle success
          console.log(response);

        })
        .catch(function (response) {
          //handle error
          console.log(response);
        });
    }
	}


  return (
    <div className='container'>
      <Link to="/">Home</Link>
      <form id="loginForm" method="POST" action="/#" >
        <legend>
          <fieldset>Seller Form</fieldset>
          <div className="formBox">
            <label for="name">Title</label>
					  <input
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
          </div>
          <div className="formBox">
            <label for="position">Type</label>
					  <input
						  value={inputText.type}
              class="pass"
              type="text"
              autocomplete="off"
              name="type"
              onChange={handleChange}
            />
          </div>
          <div class="formBox">
            <div className="formBox">
              <label for="price">Price</label>
						  <input
							  value={inputText.price}
                class="pass"
                type="text"
                autocomplete="off"
                name="price"
                onChange={handleChange}
              />
              <input type="file" multiple name="img" onChange={handleChange} />
            </div>
            <div class="formBox"></div>
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </legend>
		  </form>
		  {Message.text}
    </div>
  );
};

export default SellerForm;
