import React,{useState} from "react";

const ImgTest = ({ el, i }) => {
  const [hover, sethover] = useState(false)
console.log("hover",hover)

  return (
    <>
      <div className="cardViwersBord col col-sm-6" onMouseEnter={()=> sethover(false)} onMouseLeave={()=> sethover(true)}>
        <h1 className={hover ? `wasHoverd titleCardS titleCardS${i}` : `inHover titleCardS titleCardS${i}`}>{el.scetion}</h1>
        <div className={`imgShadowCard imgShadowCard${i}`} style={{ "backgroundImage": `url(${el.img})` }} />

        <div className="textCard">
          <div className={`titleCard titleCard${i}`}>
            <h3>{el.title}</h3>
            <p>{el.par}</p>
            <a href="#" style={{ "color": "#06c" }}>More infos</a>
            <a href="#" style={{"color":"#06c"}}>Buy now</a>
          </div>
          <img className={`imgCardIndex${i}`} width="100%" src={el.img} alt={el.title} />
        </div>
      </div>
    </>
  );
};

export default ImgTest;
