import { React,useReducer } from "react";
import { Link } from "react-router-dom";
import { basketReducer } from "../reducers/reducer";

const BasketList = ({ oneItem, dispatch,basket }) => {




  return (
    <>
      <div key={oneItem._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: oneItem.website ? "" : "red" }}
        >
          <Link to={`item/${oneItem._id}`}>
            <div className="card-header">
              {oneItem.img == undefined
                ? ""
                : oneItem.img.map((e) => (
                    <img
                      key={oneItem._id}
                      style={{ maxWidth: "100%" }}
                      src={"http://localhost:3001/" + e}
                      alt={oneItem.name}
                    />
                  ))}
            </div>
            <div className="card-body">
              <h2>{oneItem.title}</h2>
              <h5>{oneItem.category}</h5>
              <div>{oneItem.price}</div>
              <div>{oneItem.quantity}</div>
              <div>{basket.map(el=> el.total)}</div>
            </div>
          </Link>

        </div>
      </div>
    </>
  );
};

export default BasketList;
