import React, { useState, useEffect } from "react";
import "./item.css";

import ImageGallery from 'react-image-gallery';
const Image = ({ el, i }) => {
  return (
    <>
      <div className="item">
        <div className="current">
          {el && el.img
            ? el.img
                .map((img) => (
                  <img
                    src={"http://localhost:3001/" + img.original}
                    alt={el.title}
                  />
                ))
                .slice(0, 1)
            : ""}
        </div>
        <div className="thumb">
          {el && el.img
            ? el.img.map((img) => (
                <img
                  src={"http://localhost:3001/" + img.original}
                  alt={el.title}
                />
              ))
            : ""}
        </div>
      </div>
    </>
  );
};

export default Image;
