import { React, useContext } from "react";
import BasketList from "./BasketList";
import Buy from "./Buy";
import { prodactsConext } from "../context/contextData";

const BaskeApi = () => {
  const { basket, dispatch, user } = useContext(prodactsConext);
const {items, total} = basket


  return (
    <div>
      <h1 className="text-center">Basket</h1>
      {items.length ? (
        <div className="container">
          <div className="row">
            {items.map((el) => {
              return (
                <BasketList
                  key={el._id}
                  oneItem={el}
                  dispatch={dispatch}
                  items={items}
                  user={user}
                />
              );
            })}
          </div>
          <div className="text-danger">Total Price: {total}</div>
          <Buy card={items} />
        </div>
      ) : (
        <h3 className="text-center">your Basket is Empty</h3>
      )}
    </div>
  );
};

export default BaskeApi;
