      return state;
  }
};

const totalprice = (item) => {
  var totals = 0;
      item.forEach(el => {
        totals += el.quantity * el.price
      })
  return totals
}



export const itemReducer = (state , action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      const data = action.payload
      return {
        ...data,
        quantity: 1
      };
    case "TOGGLE_WATCH":
      return {
        ...state,
        completed: !action.payload.completed,
      };
      case "TOGGLE_BASKET":
        return {
          ...state,
          basket: !action.payload.basket,
        };
    case "INCROMENT":
      return {
        ...state,
        quantity: state.quantity + 1
      }
    case "DECROMENT":
      if (state.quantity > 1) {
         return {...state,
          quantity: state.quantity - 1
            };
      } else {
       return {...state, quantity: state.quantity}
      }
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return state;
  }
};

export const usersReducer = (state, action) => {
  switch (action.type) {
    case "LOGIN_SUCCESS":
      const user = action.payload
      return {
        user,
        loggedIn:true
      };
    case "LOGOUT_USER":
      return action.payload
    case "PASS_WRONG":
      return action.payload
    case "EMAIL_WRONG":
      return action.payload
    case "GET_USER_LOCAL":
      return action.payload
    default:
      return state;
  }

}
