import { React, useContext, useEffect,useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import { Link} from "react-router-dom";

function HomeApi() {
  const { data, dispatch,user, dispatchUser } = useContext(prodactsConext);
  console.log('data', data)



console.log(user)
  useEffect(() => {
    let _id = '';

    if (!user.user) {
      _id = Date.now()
      dispatchUser({ type: "FACK_USER", payload:{ _id:_id} });
     }


     getsReq().then((result) => {
      if(user){   
      dispatch({ type: "FATCH_SUCCESS", payload:{product:result.data, userId: _id} })

      } else {
        dispatch({ type: "FATCH_SUCCESS", payload:{product:result.data, userId: user.user._id} })
          }
     })
  },[]);

  return (

    <div className="container">

      <div className="row">
        {data.product ? data.product.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              data={data}
              setData={dispatch}
            />
          );
        }): 'loading'}
      </div>
    </div>
  );
}

export default HomeApi;
