import { React, useState, useEffect, useContext } from "react";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import { getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import CheckLogIn from "../CheckLogIn";
import Image from "./Image";
import Tabs from "./Tabs";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";
import { gsap } from "gsap";

gsap.registerPlugin(MotionPathPlugin);

const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
  const {
    dispatch1,
    user,
    dispatchOneItem,
    oneItem,
    dispatchWatch,
    watchItem,
  } = useContext(prodactsConext);

  const [opened, setOpened] = useState(0);

  const [inPlace, setInPlace] = useState(0);
  const [disabled, setDisabled] = useState(false);

  const onClick = (index) => {
    if (!disabled) setOpened(index);
  };
  const onInPlace = (index) => setInPlace(index);

  const next = () => {
    let nextIndex = opened + 1;
    if (nextIndex >= oneItem.img.length) nextIndex = 0;
    onClick(nextIndex);
  };

  useEffect(() => setDisabled(true), [opened]);
  useEffect(() => setDisabled(false), [inPlace]);
  // React.useEffect(() => {
  // 	if(CodePen && CodePen.isThumbnail)
  // 	{
  // 		setTimeout(() => next(), 100)
  // 	}
  // }, [])

  /*
  {oneItem.img == undefined
    ? ""
    : oneItem.img.map((e) => (
        <img
          style={{ maxWidth: "100%" }}
          src={"http://localhost:3001/" + e}
          alt={oneItem.name}
        />
    ))}
*/
  useEffect(() => {
    getReq(id).then((result) => {
      dispatchOneItem({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, [id, edit]);

  console.log("item", oneItem);

  return (

   <>
                <div className="containerI">
                  {oneItem.img == undefined
                    ? ""
                    : oneItem.img.map((image, i) => (
                        <div
                          key={image.url}
                          className="image"
                          style={{
                            zIndex: inPlace === i ? i : oneItem.img.length + 1,
                          }}
                        >
                          <Image
                            total={oneItem.img.length}
                            id={i}
                            url={"http://localhost:3001/" + image}
                            title={oneItem.title}
                            open={opened === i}
                            inPlace={inPlace === i}
                            onInPlace={onInPlace}
                          />
                        </div>
                      ))}
                  <div className="tabs">
                    <Tabs
                      className="tabs"
                      images={oneItem.img}
                      onSelect={onClick}
                    />
                  </div>
                </div>

                <button className="button next shadow" onClick={next}>
                  <svg width="24" height="24" viewBox="0 0 24 24">
                    {" "}
                    <path d="M4,11V13H16L10.5,18.5L11.92,19.92L19.84,12L11.92,4.08L10.5,5.5L16,11H4Z" />{" "}
                  </svg>
                </button>
      </>     


  );
};

export default Item;
