import React, { useState, useEffect } from "react";
import "./item.css";
import ImageGallery from 'react-image-gallery';


const Image = ({ el, i }) => {
	{el && el.img
            ? el.img
                .map((img) => (
                  <img
                    src={"http://localhost:3001/" + img.original}
                    alt={el.title}
                  />
                ))
                .slice(0, 1)
		: ""
	}
const images =
		el && el.img
            ? el.img.map((img) => (
                <img
                  src={"http://localhost:3001/" + img.original}
                  alt={el.title}
                />
              ))
            : ""
  return (
	  <>
		  <ImageGallery items={images} />
      <div className="item">
        <div className="current">

        </div>
        <div className="thumb">

        </div>
      </div>
    </>
  );
};

export default Image;
