import React, { createContext, useReducer,useEffect } from 'react';
import { reducer,basketReducer,usersReducer,itemReducer,watchReducer } from "../reducers/reducer";
import {asyncLocalStorage} from'../api/rest-helper'
export const prodactsConext = createContext();

export const ProductsProvider = props => {
    const [data, dispatch] = useReducer(reducer, []);
    const [basket, dispatch1] = useReducer(basketReducer, { items: [], total: 0 });
    const [user, dispatchUser] = useReducer(usersReducer, []);
    const [oneItem, dispatchOneItem] = useReducer(itemReducer, false);
    const [watchItem, dispatchWatch] = useReducer(watchReducer, []);
    useEffect(() => {
        getLocal()
        getLocal1()
        getLocal2()
    },[])

    useEffect(() => {
        saveLocal()

    }, [user, basket, watchItem])

    const saveLocal = () => {
        localStorage.setItem('watch', JSON.stringify(watchItem));
        localStorage.setItem('basket', JSON.stringify(basket));
        localStorage.setItem('user', JSON.stringify(user));
    }

    const getLocal = () => {
        if (localStorage.getItem('user') === null) {
            localStorage.setItem('user', JSON.stringify([]))
        } else {
            let userLocal = JSON.parse(localStorage.getItem('user'));
            dispatchUser({type:'GET_USER_LOCAL', payload:userLocal});
        }
    }

    const getLocal1 = () => {
        if (localStorage.getItem('basket') === null) {
            localStorage.setItem('basket', JSON.stringify([]))
        } else {
            let basketLocal = JSON.parse(localStorage.getItem('basket'));
            dispatch1({type:'GET_BASKET_LOCAL', payload:basketLocal});
        }
    }

    const getLocal2 = () => {
        if (localStorage.getItem('watch') === null) {
            localStorage.setItem('watch', JSON.stringify([]))
        } else {
            let watchLocal = JSON.parse(localStorage.getItem('watch'));
            dispatchWatch({type:'GET_WATCH_LOCAL', payload:watchLocal});
        }
    }

    let userIde = '';
    if (user.loggedIn == true) {
     userIde = user.user._id
   }



    return (
        <prodactsConext.Provider value={{data, dispatch, basket, dispatch1, user, userIde, dispatchUser,oneItem, dispatchOneItem,dispatchWatch, watchItem }}>
           {props.children}
        </prodactsConext.Provider >
   );
}
