import { React } from "react";
import { Link } from "react-router-dom";


const BasketList = ({ oneItem, dispatch }) => {


  return (
    <>

        <div key={oneItem._id} className="col-md-4 col-sm-6">
          <div
            className="card"
          >
            <Link to={`item/${oneItem._id}`}>
              <div className="card-header">
                {oneItem.img == undefined
                  ? ""
                  : oneItem.img.map((e) => (
                    <img
                      key={oneItem._id}
                      style={{ maxWidth: "100%" }}
                      src={"http://localhost:3001/" + e}
                      alt={oneItem.name}
                    />
                  )).slice(0, 1)}
              </div>
              <div className="card-body">
                <h2>{oneItem.title}</h2>
                <h5>{oneItem.category}</h5>
                <div>{oneItem.price}</div>
                <div>{oneItem.quantity * oneItem.price}</div>
                <div>{oneItem.quantity}</div>
              </div>
          </Link>
          <button onClick={()=> dispatch({type:"DELETE_ITEM", payload:oneItem})}>Delete</button>
            <button onClick={() => dispatch({ type: 'DECROMENT', payload: oneItem })}> - </button>
            <button onClick={() => dispatch({ type: 'INCROMENT', payload: oneItem })}> + </button>
          </div>
        </div>
    </>
  );
};

export default BasketList;
