import { React, useContext, useEffect, useState,useRef } from "react";
import Slider from "react-slick";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from "../recent/RecentList";
import DescountsList from "../discounts/DiscountsList";
import SliderScreen from "./SliderScreen";
import ImgTest from './ImgTest'
import Footer from '../footer/Footer'
import AnimateScreen from "../AnimateScreen";
import { Parallax, ParallaxLayer, IParallax } from '@react-spring/parallax'
import styles from './styles.module.css'
import Page from "./Page";
function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);



  // Creat Fack User if there is no real User
  if (!user.user) {
    const _id = Date.now();
    dispatch({ type: "FACK_USER", payload: { _id: _id } });
  }

  useEffect(() => {
    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, []);

  let settings = {
    className:"slideCard",
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    slidesToShow: 6,
    slidesToScroll: 1
  };

  let settings1 = {
    className:"slideCardS1",
    dots: true,
    infinite: true,
    speed: 2000,
    autoplay: true,
    autoplaySpeed: 4000,
    slidesToShow: 1,
    cssEase: "ease-out",
    slidesToScroll: 1
  };

  const imagesTest = [ 
   { img: "/images/Apple/apple5.jpg",title:"Apple Watch", par:"International Collection"},
    {img: "/images/Apple/apple4.jpg", title:"iMac", par:"Say hello"},
   { img: "/images/svg/nour1.jpg", title:"", par:""},
   { img: "/images/svg/Kitchen.jpg", title:"", par:""},
   { img: "/images/svg/Living_room_Wall_1.jpg", title:"", par:""},
   { img:"/images/svg/digital-laptop-working-global-business-concept.jpg", title:"", par:""}
  ]

  const imagesSlide = [ 
    { img: "/images/Apple/apple1.jpg",title:"iPhone 12", par:"International Collection"},
     {img: "/images/samsung/01.jpg", title:"Samsung", par:"Ultimate Collectios"},
    { img: "/images/svg/Untitled-1.jpg", title:"Huawei Mate", par:"life your Moment"}
  ]

  const images5 = [ 
    { img: "/images/samsung/galaxy2.jpg",title:"Apple Watch", par:"International Collection"},
    { img: "/images/samsung/bet room.jpg", title:"", par:""},
    { img: "/images/ikea/ein-bettgestell.jpg", title:"", par:""},
    { img: "/images/svg/dining-tabel1.jpg", title:"", par:""},
    { img:"/images/svg/white-livingroom.jpg", title:"", par:""}
   ]

  return (
    <div className="container1">

         <Slider {...settings1}>  
      {imagesSlide.map((el, i)=>  <SliderScreen el={el} i={i} /> )}
      </Slider>

        <div className="textCardViwer">
          <div>
             <h1>iPad Pro</h1>
            <p>Supercharged By Apple M1 chip</p>
          </div>
        <img width="100%" src="/images/Apple/apple3.jpg" alt=""/>
      </div>
      <div className="row">
        {imagesTest.map(el => <ImgTest el={el} />)}
        </div>

        <Slider {...settings}>  
     {products.map((event) => {
              return <Cards key={event._id} card={event} dispatch={dispatch} />;
            })
        }

      </Slider>
      <div class="row">
    <div class="col big-box col-3">
      <img width="100%" src={images5[0].img} alt=""/>
    </div>
    <div class="col col-lg-9">
      <div class="row">
        <div class="col "><div className="colBox"><h3>Sleep Room</h3><p>live your life</p></div><img width="100%" src={images5[1].img} alt=""/></div>
        <div class="col "><div className="colBox"><h3>Modern</h3><p>Dream table</p></div><img width="100%" src={images5[3].img} alt=""/></div>
      </div>
      <div class="row">
        <div class="col "><div className="colBox"><h3>My Energize and...</h3><p>Relaxing Home</p></div><img width="100%" src={images5[2].img} alt=""/></div>
        <div class="col "><div className="colBox"><h3>Feel Confortable</h3><p>Living room</p></div><img width="100%" src={images5[4].img} alt=""/></div>
          </div>

    </div>


  </div>
          <div className="sections row mt-3">   
        <RecentList />
        <DescountsList />
        <Parallax className={styles.container} ref={parallax} pages={3} horizontal>
        <Page offset={0} gradient="pink" onClick={() => scroll(1)} />
        <Page offset={1} gradient="teal" onClick={() => scroll(2)} />
        <Page offset={2} gradient="tomato" onClick={() => scroll(0)} />
      </Parallax>
      </div>
      <Footer/>
    </div>
  );
}

export default HomeApi;
