import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

const SellerForm = () => {
  const [inputText, setInputText] = useState({
    title: "",
    type: "",
    price: "",
    img:[]
  });

  const [addProduct, setAddProduct] = useState(false);
	const [Message	, setMessage] = useState([]);
  const [isData, setisData] = useState(false)
 // const [img, setimage] = useState([])

/*
const handleImage = (e) => {
  [...e.target.files[0]].forEach(e => {
        let imge = URL.createObjectURL(e)
        inputText.img.push(imge)
      });

}
*/

console.log('img', inputText.img)
  const handleChange = (e) => {
    const value = e.target.value;
     setInputText({ ...inputText, [e.target.name] : e.target.name == 'img' ? e.target.file : value });
    setisData(true)
  };

console.log('isData', isData)

  console.log("addProduct", addProduct);

  const handleSubmit = (e) => {
    e.preventDefault();
   const onDta = new FormData();
    for (let key in addProduct){
      if (key == 'img') {
    onDta.append(key, addProduct[key], addProduct[key].name)
      } else {
        onDta.append(key, addProduct[key])
  }
    } 
    axios.post(`http://localhost:3001/products/`, onDta )
    .then(result => {
      setAddProduct(result.data)
        console.log('result',result.data)
    })

    if (isData === true) {
     setAddProduct(inputText);
      setInputText({
        title: "",
        type: "",
        price: "",
      })
      setisData(false)
      setMessage({ text: 'The data has been sent successfully' })
    } else {
     setMessage({ text: 'add a prodact' })
    }
	 setTimeout(() => {
    setMessage([])
   }, 2000);
  };







  return (
    <div className='container'>
      <Link to="/">Home</Link>
      <form id="loginForm" method="POST" action="/#" >
        <legend>
          <fieldset>Seller Form</fieldset>
          <div className="formBox">
            <label for="name">Title</label>
					  <input
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
          </div>
          <div className="formBox">
            <label for="position">Type</label>
					  <input
						  value={inputText.type}
              class="pass"
              type="text"
              autocomplete="off"
              name="type"
              onChange={handleChange}
            />
          </div>
          <div class="formBox">
            <div className="formBox">
              <label for="price">Price</label>
						  <input
							  value={inputText.price}
                class="pass"
                type="text"
                autocomplete="off"
                name="price"
                onChange={handleChange}
              />
              <input type="file" multiple name="img" onChange={handleImage} />
            </div>
            <div class="formBox"></div>
            <button onClick={handleSubmit}>Submit</button>
          </div>
        </legend>
		  </form>
		  {Message.text}
    </div>
  );
};

export default SellerForm;
