import {React, useState} from 'react'
import { reducer } from "./reducer";

export const useFetch = (id) => {

    const [oneItem, dispatch] = useReducer(reducer, false);

    useEffect((id) => {
        axios
        .get(`http://localhost:3001/products/${id}`)
        .then((result) => dispatch({type:'FATCH_SUCCESS', payload: result.data }));
    }, [])

return [oneItem, dispatch]
}
