import React, { useRef, useState, useEffect } from "react";
import Tilt from "react-parallax-tilt";
import HartsLove from "./HartsLove";
import { usePalette } from 'react-palette'


function Card3D({ el }) {

  const [like, setlike] = useState(false)
  const image = () => {
     {el.img.map(imeEl => { return "http://localhost:3001/" + imeEl.original})} 
  }



  return (
    <Tilt className="TiltCard3D"   glareEnable={false} glareMaxOpacity={0.8} glareColor="#ffffff" glarePosition="bottom" glareBorderRadius="20px">
      <div class="card3D" >
       <img src={image} alt=""/>)} 
       {el.img.map(imeEl => <div className="shadow3d" style={{ "backgroundImage": `url(${"http://localhost:3001/" + imeEl.thumb})` }}/>).slice(0,1)} 
              <div class="inner-card" >
          <h2>{el.price}</h2>
          <h3>{el.title}</h3>
          <i onClick={() => setlike(!like)}  class={like ? "fas fa-heart" : "far fa-heart"}></i>
        </div>
        <span >

        </span>
        <HartsLove like={like}/>
        </div>
    </Tilt>
  );
}

export default Card3D;
