import {React, useState} from 'react'
import axios from "axios";


const Edit = ({ key, card, setNewData,  setEdit, newData}) => {

    const [input, setInput] = useState("");
    const [gitOneData, setGitOneData] = useState(false);

    if ( !gitOneData ){
        setGitOneData(true)
       axios.get(`http://localhost:3001/products/${key}`)
        .then( result => setNewData(result.data) );
      }

    const handleChange = e => 
        setInput({...input,
       [e.target.name] : e.target.value
    }

        );



    const handleSubmit = e => {
    e.preventDefault();
    setNewData(input);
       setEdit(false)
    };

    console.log('input', input);


    return (
        <>

    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
        <div className="card-body">
        <input
            placeholder={card.title}
            value={input.title}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
        />
          <h5>{card.position}</h5>
          <input
            placeholder={card.price}
            value={input.price}
            onChange={handleChange}
            name='price'
           // ref={inputRef}
            className='todo-input edit'
        />
          <div></div>
          <div></div>
        </div>


      </div>
    </div>


          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
        </>
    )
}

export default Edit
