import {React, useState, useEffect} from "react";
import Edit from './Edit'
import axios from "axios";
import AddToBasket from "./AddToBasket";

const Cards = ({card, data, setData,}) => {
  const [edit, setEdit] = useState(false);
  const [favorate, setfavorate] = useState([])
  useEffect(() => {
    setfavorate(data.filter(event => event.completed === true))
 }, [data])


  useEffect(() => {
    axios.put(`http://localhost:3001/products/${card._id}`, favorate).then((result) => {
      console.log("result toggle", result.data);
    });
  }, [favorate])


const editHandle = (e) => {
  return (
      <>
      <Edit setEdit={setEdit} card={card} data={data} setData={ setData}/>
         </>
    );
}

const addToWachList = (card) => {

  setData(data.map(el => {
    if (el._id === card._id) {
        return {
            ...el, completed: !el.completed
        }
    }
    return el
  }))

}
console.log('favorate',favorate)

  return (
    <>
      {edit ? editHandle() :
    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
            <div className="card-header">
            {card.img.map(e=> <img style={{ maxWidth: "100%" }} src={'http://localhost:3001/'+e} alt={card.name} />)}

        </div>
        <div className="card-body">
         <h2 >{card.title}</h2>
          <h5>{card.type}</h5>
          <div>{card.price}</div>
          <div></div>
          <div></div>
            </div>
            <button
            onClick={() => setEdit(true)}
            className='btn btn-secondary'> update</button>
            <button onClick={()=> addToWachList(card)} >add to favorate</button>
            <AddToBasket key={card._id} card={ card}/>
      </div>
      </div>}
      </>
  );
};

export default Cards;
