import React, { useState, useContext } from "react";
import axios from "axios";
import { Redirect } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import { useForm } from "react-hook-form";

const Login = () => {
  const [redirect, setRedirect] = useState(false);
  const { user, dispatchUser } = useContext(prodactsConext);
  const [err, setErr] = useState("");
  const {
    register,
    formState: { errors },
    handleSubmit,
  } = useForm();

  const submit = (data) => {
    console.log(data);

    axios
      .post(`http://localhost:3001/users/login`, data)
      .then((result) => {
        console.log("result", result);
        dispatchUser({ type: "LOGIN_SUCCESS", payload: result.data });
        const token = result.headers["x-auth"];
        axios.defaults.headers.common["x-auth"] = token;
        console.log("token", token);
        if (result.status == 200) {
          setRedirect(true);
        }
      })
      .catch((error) => {
        console.log(error.response.status);
        if (error.response.status === 404) {
          setErr("Email is Not Registed");
        } else if (error.response.status === 401) {
          setErr("Wrong Passward");
        }
      });
  };

    const test = (inputText) => {
        const onData = new FormData();
        for (let key in inputText) {
          if (key == "img") {
            inputText[key].forEach((fileImg) => {
              if (typeof inputText[key] == "object")
                onData.append(key, fileImg, fileImg.name);
            });
              console.log(fileImg)
          } else {
            onData.append(key, inputText[key]);
          }
        }
    }


  return (
    <div>
      {redirect ? <Redirect to="/" /> : ""}
      <div className="container">
        <h2 className="text-center">Login</h2>
        <form action="#" onSubmit={handleSubmit(test)}>
          <label htmlFor="email">
            <input
              {...register("email", { required: true })}
              id="email"
              placeholder="email"
            />
          </label>
          {errors.email && <span>This field is required</span>}
          <label htmlFor="password">
            <input
              {...register("password", { required: true })}
              id="password"
              placeholder="password"
            />
            {errors.password && <span>This field is required</span>}
          </label>
          <label htmlFor="img">
            img
            <input
              {...register('img')}
              type="file"
                          id="img"
                          multiple
              placeholder="umg"
            />
            {errors.img && <span>This field is required</span>}
          </label>
          <div className="text-center text-danger">{err}</div>
          <button type="submit" className="green">
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};

export default Login;
