import {React, useState,useEffect} from 'react'
import axios from 'axios'
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    useRouteMatch,
    useParams,
  } from "react-router-dom";


const AddToWatchList = ({ data, setData }) => {
    const [taggle, settaggle] = useState([])




    const addToWachList = (callBack) => {  
        settaggle({ ...taggle, completed: !data.completed })
        setTimeout(() => {
            callBack()
        }, 1000);

        }

        const completesHandle = () => {
            axios.put(`http://localhost:3001/products/${data._id}`, taggle).then((result) => {
            console.log("result toggle", result.data);
            });
        }

    return (
        <div>
            <button className='btn' onClick={() => addToWachList(completesHandle)}>add to watch</button>
        </div>
    )
}

export default AddToWatchList
