import {React, useState, useEffect} from "react";


const Cards = ({ card, data, setData }) => {
  const [edit, setEdit] = useState({
    id: null,
    value: ''
  });

  const [input, setInput] = useState(edit ? {
    title: "",
    position: "",
    price: "",
  } : '');
 // const inputRef = useRef(null);

  useEffect(() => {
   // inputRef.current.focus();
  });

console.log(edit)


  const deleteHandler = (e) => {
    setData(data.filter(el => el.id !== card.id))
  }

  const submitUpdate = value => {
    updateTodo(edit.id, value);
    setEdit({
      id: null,
      value: ''
    });
  };
  const updateTodo = (dataId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }
    const newEdit = data.map(item => {
      if (item._id === dataId) {
      item.title = newValue.text
      } 
        return item
    })
    setData(newEdit)
  };

  const handleChange = e => {
    setInput(...data,{[e.target.name] : e.target.value });
  };
  console.log('input', input)

  const handleSubmit = e => {
    e.preventDefault();
    setData(input);
    setInput('');
  };



const editHandle = (e) => {
  return (
      <form >
      <>
          <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
        />
        <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='price'
           // ref={inputRef}
            className='todo-input edit'
          />
          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
         </>
         </form>
    );
}

  const updateButton = (e) => {
    if (!edit.value) {
     return (
     <button
          onClick={() => setEdit({ id: card._id, value: e })}
          className='edit-icon'
       > update</button>
  )
  }
}



  return (
    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
        <div className="card-body">
         {edit.value ? editHandle() : <h2 >{card.title}</h2>}{updateButton(card.title)}
          <h5>{card.position}</h5>
          {edit.value ? editHandle() : <div>{card.price}</div>}{updateButton(card.price)}
          <div></div>
          <div></div>
        </div>

        <button onClick={deleteHandler} className="trash-btn">
                    <i className="fas fa-trash"></i>
        </button>
      </div>
    </div>
  );
};

export default Cards;
