import {React, useState} from 'react'

const Edit = ({ card, data, setData}) => {

    const [input, setInput] = useState([]);

    const handleChange = e => 
    setData({...data,
      [e.target.name]: e.target.value
    });

  console.log('input', input)

  const handleSubmit = e => {
    e.preventDefault();
    setData(...data,input);
    setInput('');
  };

    return (
        <>

    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
                    <div className="card-body">
                    <input
            placeholder='Update your item'
            value={card.title}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
        />
         {edit.id ? editHandle() : <h2 >{card.title}</h2>}{updateButton()}
          <h5>{card.position}</h5>
          {edit.id ? editHandle() : <div>{card.price}</div>}{updateButton()}
          <div></div>
          <div></div>
        </div>

        <button onClick={deleteHandler} className="trash-btn">
                    <i className="fas fa-trash"></i>
        </button>
      </div>
    </div>
          <form >
          <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='title'
           // ref={inputRef}
            className='todo-input edit'
        />

          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
         </form>  
        </>
    )
}

export default Edit
