import React from 'react'
import { Link } from "react-router-dom";

const DiscountsScreen = ({ card }) => {

    return (
        <>
          <div key={card._id} className="col-md-2 col-sm-8">
            <div className="card cardV">
              <Link to={`item/${card._id}`}>
                <div className="card-header1">
                  {card.img
                    .map((e) => (
                      <img
                        key={card._id}
                        style={{ maxWidth: "100%" }}
                        src={"http://localhost:3001/" + e}
                        alt={card.name}
                      />
                    ))
                    .slice(0, 1)}
                </div>
                <div className="card-body">
                <h6>{card.title}</h6>
                <div>{card.category}</div>
                <div style={{textDecoration:line_Through}}>{card.price} €</div>
                <div>{card.oldPrice} €</div>
                </div>
              </Link>
            </div>
          </div>
        </>
      );  
}

export default DiscountsScreen
