import { React, useState, useEffect, useReducer } from "react";
import { itemReducer,basketReducer } from "../reducers/reducer";
import Edit from "./Edit";
import AddToBasket from "../basket/AddToBasket";
import { putReq, getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";

const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
  const [oneItem, dispatch] = useReducer(itemReducer, false);
  const [state, dispatch1] = useReducer(basketReducer, { price: oneItem.price,qantity:1  })

  useEffect(() => {
      getReq(id).then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, [edit]);

  useEffect(() => {
    putReq(oneItem, oneItem._id);
    dispatch1({price: oneItem.price})
  }, [oneItem]);

  console.log("item", oneItem);



  return (
    <div className="container">
      <div className="row">
        <h1>Item </h1>
        {edit ? (
          <Edit key={oneItem._id} setEdit={setEdit} dispatch={dispatch} />
        ) : (
          <div className="col-md-4 col-sm-6">
            <div
              className="card"
              style={{ borderColor: oneItem.website ? "" : "red" }}
            >
              <div className="card-header">
                {oneItem.img == undefined
                  ? ""
                  : oneItem.img.map((e) => (
                      <img
                        style={{ maxWidth: "100%" }}
                        src={"http://localhost:3001/" + e}
                        alt={oneItem.name}
                      />
                    ))}
              </div>
              <div className="card-body">
                <h2>{oneItem.title}</h2>
                <h5>{oneItem.category}</h5>
                <div>{state.price}</div>
              </div>
              <button
                onClick={() => setEdit(!edit)}
                className="btn btn-secondary"
              >
                {" "}
                update
              </button>

              <AddToBasket key={oneItem._id} card={oneItem} />
              <button
                className="btn"
                onClick={() =>
                  dispatch({ type: "TOGGLE_WATCH", payload: oneItem })
                }
              >
                {oneItem.completed == false
                  ? "Add to Watch"
                  : "Remove from watch List"}
                </button>
                <button
                className="btn"
                onClick={() =>
                  dispatch({ type: "TOGGLE_BASKET", payload: oneItem })
                }
              >
                {oneItem.basket == false
                  ? "Add to Basket"
                  : "Remove from Basket List"}
                </button>
                <button style={{display: oneItem.price == state.price  ? 'none' : 'block' }} onClick={()=> dispatch1({type: 'DECROMENT', payload:oneItem.price})}> - </button>
          <button onClick={()=> dispatch1({type: 'INCROMENT', payload:oneItem.price,  qantity:1})}> + </button>
            </div>
          </div>

        )}
      </div>
    </div>
  );
};

export default Item;
