import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

const SellerForm = () => {
  const [inputText, setInputText] = useState({img:[]});



  const handleImage = (e) => {
    [...e.target.files].forEach((el) => {
      inputText.img.push(el)
    })
  };

  console.log('inputText', inputText);

  const handleChange = (e) => {
    setInputText({
      ...inputText,
      [e.target.name]: e.target.name == 'img' ? e.target.files[0] : e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

   const onData = new FormData();
    for(let key in inputText){
      if (key == 'img') {
        inputText[key].forEach(fileImg => {
         console.log('image',fileImg )
           if (typeof inputText[key] == 'object') onData.append(key, fileImg, fileImg.name)
        })


      } else {
        onData.append(key, inputText[key])
      }
    }
    console.log('ondata',onData)
    axios.post(`http://localhost:3001/products/`, onData )
    .then(result => {
        console.log('result',result.data)
    })
  };



  return (
    <div className='container'>
      <Link to="/">Home</Link>

  <div className="segment">
    <h1>Selling</h1>
  </div>
  <form action="">
  <label>
          <input
            placeholder="Title"
						 value={inputText.title} 
              className="email"
              type="text"
              autocomplete="off"
              name="title"
              onChange={handleChange}
            />
  </label>
  <label>
          <input
            placeholder="Catorgy"
						  value={inputText.type}
              class="pass"
              type="text"
              autocomplete="off"
              name="type"
              onChange={handleChange}
            />
        </label>
        <label>
          <input
            placeholder="Price"
            value={inputText.price}
            class="pass"
            type="text"
            autocomplete="off"
            name="price"
            onChange={handleChange}
            />
        </label>
        <label for="file">choose a file</label>

         <input id="file" class="custom-file-input"  type="file" multiple name="img" onChange={handleImage} />  




  <div className="input-group">
    <label>
      <input  type="text" placeholder="Description"/>
    </label>

  </div>
  <button onClick={handleSubmit} className="red" type="button"><i className="icon ion-md-lock"></i>Submit</button>

</form>
    </div>
  );
};

export default SellerForm;
