import {React, useRef, useState, useEffect} from "react";


const Cards = ({ card, data, setData }) => {
  const [edit, setEdit] = useState({
    id: null,
    value: ''
  });

  const [input, setInput] = useState(edit ? edit.value : '');

  useEffect(() => {
   setData([{title:edit}])
  }, [edit])

console.log(edit)


  const deleteHandler = (e) => {
    setData(data.filter(el => el.id !== card.id))
  }

  const submitUpdate = value => {
    updateTodo(card._id, value);
    setEdit({
      id: null,
      value: ''
    });
  };
  const updateTodo = (dataId, newValue) => {
    if (!newValue.text || /^\s*$/.test(newValue.text)) {
      return;
    }

    setData(prev => prev.map(item => (item.id === dataId ? newValue : item)));
  };

  const handleChange = e => {
    setInput(e.target.value);
  };



  const handleSubmit = e => {
    e.preventDefault();
    const value = {
      id: Math.floor(Math.random() * 10000),
      text: input
    }
    submitUpdate(value);
    setInput('');
  };




   if (edit.id) {
    return (
      <>
          <input
            placeholder='Update your item'
            value={input}
            onChange={handleChange}
            name='text'

            className='todo-input edit'
          />
          <button onClick={handleSubmit} className='todo-button edit'>
            Update
          </button>
        </>
    );
  }


  return (
    <div className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
        <div className="card-header">
          <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
        </div>
        <div className="card-body">
          <h2 >{card.title}</h2>
          <h5>{card.position}</h5>
          <div>{card.price}</div>
          <div></div>
          <div></div>
          <button
          onClick={() => setEdit({ id: card._id, value: card.title })}
          className='edit-icon'
        > click for update</button>
        </div>

        <button onClick={deleteHandler} className="trash-btn">
                    <i className="fas fa-trash"></i>
                </button>
      </div>
    </div>
  );
};

export default Cards;
