  const [edit, setEdit] = useState(false);


  const deleteHandler = (e) => {
    setData(data.filter(el => el.id !== card.id))
  }

const editHandle = (e) => {
  return (
      <>
      <Edit setEdit={setEdit} card={card} data={data} setData={ setData}/>
         </>
    );
}

  const updateButton = (e) => {
    if (!edit) {
     return (
     <button
          onClick={() => setEdit(true)}
          className='btn btn-secondary'
       > update</button>
  )
  }
}

 //{card.img.map(e=> <img style={{ maxWidth: "100%" }} src={e} alt={card.name} />)}

  return (
    <>
      {edit ? editHandle() :
    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
            <div className="card-header">

            <img style={{ maxWidth: "100%" }} src={'http://localhost:3001' +card.img} alt={card.name} />
        </div>
        <div className="card-body">
         <h2 >{card.title}</h2>
          <h5>{card.type}</h5>
          <div>{card.price}</div>
          <div></div>
          <div></div>
        </div>
        {updateButton()}

      </div>
      </div>}
      </>
  );
};

export default Cards;
