import React,{useState, useContext} from 'react'
import axios from "axios";
import { Redirect } from "react-router-dom";
import {prodactsConext}from '../context/contextData'
import { useForm } from 'react-hook-form';


const Login = () => {
    const [inputText, setInputText] = useState([]);
    const [redirect, setRedirect] = useState(false);
    const {login} = useForm();
    const { user, dispatchUser } = useContext(prodactsConext)
    const [err, setErr] = useState('')
const {register, formState:{errors} } = useForm()
    const handleChange = (e) => {
           setInputText({...inputText,[e.target.name]: e.target.value,});
      };

    const handleSubmit = (e) => {
       // axios.defaults.headers.common['x-auth'] = token 
        axios
            .post(`http://localhost:3001/users/login`, inputText,)
            .then((result) => {
            console.log("result", result);
            dispatchUser({ type: "LOGIN_SUCCESS", payload: result.data })
            const token = result.headers['x-auth']
            console.log('token', token)
            if (result.status == 200) {
                setRedirect(true)
            }})
            .catch(error => {
            console.log(error.response.status)
            if (error.response.status === 404) {
            //    dispatchUser({type:"EMAIL_WRONG",  payload:"Email is Not Registed"})
                setErr('Email is Not Registed')
            } else if (error.response.status === 401) {
             //   dispatchUser({type:"PASS_WRONG",  payload:"Wrong Passward"}) 
                setErr('Wrong Passward')
            }  
        })

    }

   console.log(user)

    return (
        <div>
        {redirect ? <Redirect to="/" /> : ""}
             <div className='container'>
         <h2 className='text-center'>Login</h2>
      <form action="#">
        <label htmlFor="email">
            <input
              {...register('email', {required: true})}              
            id="email"
            placeholder="email"
            value={inputText.email}
            type="text"
            name="email"
            onChange={handleChange}
                        />
                          <br />
        {formstate.errors.email && <span>This field is required</span>}
        <br />
        </label>

        <label htmlFor="password">
        <input
            {...register('password', {required: true})}
            id="password"
            placeholder="password"
            value={inputText.password}
            type="text"
            name="password"
            onChange={handleChange}
          />
                    </label>
                    <div className='text-center text-danger'>{err}</div>
              <button onClick={handleSubmit} className="green" type="button">
          <i className="icon ion-md-lock"></i>Login
        </button>
              </form>

    </div>
        </div>
    )
}

export default Login
