import React,{useState, useEffect} from 'react'
import {motion} from 'framer-motion'

const SliderScreen = ({ el, i }) => {





    return (
      < motion.div className="textCardS" animate={{
        x: -36,
        y: 0,
        scale: 1,
        rotate: 0,
      }}>

        <div className={`slideHeader${i} slideHeader`} >
          <h3>{el.title}</h3>
          <p>{el.par}</p>
        </div>
        <img width="100%" src={el.img} alt=""/>
      </motion.div>
    )
}

export default SliderScreen
