export const reducer = (state, action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      return action.payload;
    default:
      return state;
  }
};

export const watchReducer = (state, action) => {
  switch (action.type) {
    case "GETS_DATA_COMPLETED":
      return action.payload.filter((event) => event.completed === true);
    default:
      return state;
  }
};

export const basketReducer = (state = {total:0, items:[]}, action) => {
  switch (action.type) {
    case "ADD_ITEM":
    const item = [...state.items, action.payload]
      const totalPrice = item.map(el => { return el.quantity * el.price })
      const finalPrice = totalPrice + totalPrice
      return {
        items: item,
        total: finalPrice
      }
    case "INCROMENT":
      return {
       quantity: state.quantity + 1
      };
    case "DECROMENT":
      if (state.price > action.payload) {
        return {
          quantity: state.quantity - 1
        };
      } else {
        return  {price: action.payload }
      }
    default:
      return state;
  }
};




export const itemReducer = (state , action) => {
  switch (action.type) {
    case "FATCH_SUCCESS":
      const data = action.payload
      return {
        ...data,
        quantity: 1
      };
    case "TOGGLE_WATCH":
      return {
        ...state,
        completed: !action.payload.completed,
      };
      case "TOGGLE_BASKET":
        return {
          ...state,
          basket: !action.payload.basket,
        };
    case "INCROMENT":
      return {
        ...state,
        quantity: state.quantity + 1
      }
    case "DECROMENT":
      if (state.quantity > 1) {
         return {...state,
          quantity: state.quantity - 1
            };
      } else {
       return {...state, quantity: state.quantity}
      }
    case "DELETE_ITEM":
      return action.payload;
    case "UPDATE_ITEM":
      return action.payload;
    default:
      return state;
  }
};
