import {React, useState, useEffect} from "react";
import Edit from './Edit'
import axios from "axios";
import AddToBasket from "./AddToBasket";

const Cards = ({setfavorate,favorate, card, data, setData}) => {
  const [edit, setEdit] = useState(false);


const editHandle = (e) => {
  return (
      <>
      <Edit setEdit={setEdit} card={card} data={data} setData={ setData}/>
         </>
    );
}
  const addToWachList = (e) => {
    if (favorate.length) {
      if (favorate.map(el => el._id !== card._id)) {
        setfavorate([...favorate, card])
     }
    }else if (!favorate.length){
      setfavorate([...favorate,card])
    }

}



  return (
    <>
      {edit ? editHandle() :
    <div key={card._id} className="col-md-4 col-sm-6">
      <div className="card" style={{ borderColor: card.website ? "" : "red" }}>
            <div className="card-header">
            {card.img.map(e=> <img style={{ maxWidth: "100%" }} src={'http://localhost:3001/'+e} alt={card.name} />)}

        </div>
        <div className="card-body">
         <h2 >{card.title}</h2>
          <h5>{card.type}</h5>
          <div>{card.price}</div>
          <div></div>
          <div></div>
            </div>
            <button
            onClick={() => setEdit(true)}
            className='btn btn-secondary'> update</button>
            <button onClick={()=> addToWachList()} addToWachList={addToWachList} >add to favorate</button>
            <AddToBasket key={card._id} card={ card}/>
      </div>
      </div>}
      </>
  );
};

export default Cards;
