import { React, useContext, useEffect, useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from "../recent/RecentList";
import DescountsList from "../discounts/DiscountsList";
import Slider from "./Slider";
function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);

  // Creat Fack User if there is no real User
  if (!user.user) {
    const _id = Date.now();
    dispatch({ type: "FACK_USER", payload: { _id: _id } });
  }

  useEffect(() => {
    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, []);

  return (
    <div className="container2">
      <Slider />
      <div className="sections mt-2 mb-2">
      <div className="textCard">
            <h1>iPad Pro</h1>
            <p>Supercharged By Apple M1 chip</p>
          </div>
        <img width="100%" src="/images/Apple/apple3.jpg" alt=""/>
      </div>
      <div className="d-flex mt-2">
        <div className="two">
        <div className="textCard">
            <h1>Apple Watch</h1>
            <p>International Collection</p>
          </div>
           <img width="100%" src="/images/Apple/apple5.jpg" alt=""/>
        </div>
        <div className="one">
          <div className="textCard">
            <h1>iMac</h1>
            <p>Say hello</p>
          </div>

        <img width="100%" src="/images/Apple/apple4.jpg" alt=""/>
       </div>
      </div>
      <div className="d-flex mt-2">
        <div className="two">

           <img width="100%" src="/images/svg/nour1.jpg" alt=""/>
        </div>

        <div className="one">
        <img width="100%" src="/images/svg/Kitchen.jpg" alt=""/>
       </div>
      </div>
      <div className="d-flex mt-2">
        <div className="two">

           <img width="100%" src="/images/svg/Living_room_Wall_1.jpg" alt=""/>
        </div>

        <div className="one">
        <img width="100%" src="/images/svg/accessories.jpg" alt=""/>
       </div>
      </div>
      <div className="row">
        {products.length
          ? products.map((event) => {
              return <Cards key={event._id} card={event} dispatch={dispatch} />;
            })
          : "loading"}
        <RecentList />
        <DescountsList />
      </div>
    </div>
  );
}

export default HomeApi;
