import { React, useState, useEffect, useContext } from "react";
import Edit from "./Edit";
import Buy from "../basket/Buy";
import { getReq } from "../api/rest-helper";
import { useParams } from "react-router-dom";
import { prodactsConext } from "../context/contextData";
import CheckLogIn from "../CheckLogIn";
import Image from "./Image";
import Tabs from "./Tabs";
import { MotionPathPlugin } from "gsap/MotionPathPlugin";

import { gsap } from "gsap";

gsap.registerPlugin(MotionPathPlugin);

const Item = () => {
  const { id } = useParams();
  const [edit, setEdit] = useState(false);
  const { user, dispatch, watch, product } =
    useContext(prodactsConext);

  const [opened, setOpened] = useState(0);
  const [inPlace, setInPlace] = useState(0);
  const [disabled, setDisabled] = useState(false);

  const onClick = (index) => {
    if (!disabled) setOpened(index);
  };
  const onInPlace = (index) => setInPlace(index);

  const next = () => {
    let nextIndex = opened + 1;
    if (nextIndex >= product.img.length) nextIndex = 0;
    onClick(nextIndex);
  };

  useEffect(() => setDisabled(true), [opened]);
  useEffect(() => setDisabled(false), [inPlace]);

  useEffect(() => {
    getReq(id).then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, [id, edit]);

  return (
    <div className="container containerS">
      <div className="row11">
        {edit ? (
          <CheckLogIn
            component={() => (
              <Edit
                key={product._id}
                setEdit={setEdit}
                dispatchOneItem={dispatch}
              />
            )}
          />
        ) : (
          <div className="col ">
            <h1>{product.title}</h1>

            <div className="cardS">
              <div className="containerI shadow">
                {product.img == undefined
                  ? ""
                  : product.img.map((image, i) => (
                      <div
                        key={product._id}
                        className="image"
                        style={{
                          zIndex: inPlace === i ? i : product.img.length + 1,
                        }}
                      >
                        <Image
                          total={product.img.length}
                          id={i}
                          url={"http://localhost:3001/" + image}
                          title={product.title}
                          open={opened === i}
                          inPlace={inPlace === i}
                          onInPlace={onInPlace}
                        />
                      </div>
                    ))}
                <div className="tabs">
                  <Tabs
                    className="tabs"
                    images={product.img}
                    onSelect={onClick}
                  />
                  <button className="button next shadow" onClick={next}>
                    <svg width="24" height="24" viewBox="0 0 24 24">
                      {" "}
                      <path d="M4,11V13H16L10.5,18.5L11.92,19.92L19.84,12L11.92,4.08L10.5,5.5L16,11H4Z" />{" "}
                    </svg>
                  </button>
                </div>
              </div>
              <p>{product.description}</p>
            </div>
            <div className="card-body">
              <h5>{product.category}</h5>
              <div>{product.price} â‚¬</div>
              <div>Quantity: {product.quantity}</div>
            </div>
            {user.loggedIn &&
            user.user &&
            product.owner &&
            product.owner._id == user.user._id ? (
              <button
                onClick={() => setEdit(!edit)}
                className="btn btn-secondary"
              >
                {" "}
                update
              </button>
            ) : (
              <Buy key={product._id} card={[product]} />
            )}
            <button
              className="btn"
              onClick={() => dispatch({ type: "WATCH_ITEM", payload: product })}
            >
              {watch.some((el) => el._id === product._id)
                ? "Watched"
                : "add watch"}
            </button>
            <button
              className="btn"
              onClick={() => dispatch({ type: "ADD_ITEM", payload: product })}
            >
              add to cart
            </button>
            <button onClick={() => dispatch({ type: "DECROMENT_ITEM" })}> - </button>
            <button onClick={() => dispatch({ type: "INCROMENT_ITEM" })}> + </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Item;
