import { React, useContext, useEffect,useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import { Link} from "react-router-dom";

function HomeApi() {
  const { data, dispatch, user, dispatchUser } = useContext(prodactsConext);
console.log('data',data)
 const useridntify = user.user.i_id
  useEffect(() => {

    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: {product:result.data, userId:useridntify} })
    })
  }, []);

  return (

    <div className="container">

      <div className="row">
        {data.product.length && data.userId && data.product.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              data={data}
              setData={dispatch}
            />
          );
        })}
      </div>
    </div>
  );
}

export default HomeApi;
