import { React, useContext, useEffect, useState,useRef } from "react";

import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from "../recent/RecentList";
import DescountsList from "../discounts/DiscountsList";

import ImgTest from "./ImgTest";
import Footer from '../footer/Footer'


import Slider from "react-animated-slider";
import "./slider-animations.css";
import "react-animated-slider/build/horizontal.css";

import  Card3D  from "./Card3D";




function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);

const [curent, setcurent] = useState(Number)

  // Creat Fack User if there is no real User
  if (!user.user) {
    const _id = Date.now();
    dispatch({ type: "FACK_USER", payload: { _id: _id } });
  }

  useEffect(() => {
    getsReq().then((result) => {
      dispatch({ type: "FATCH_SUCCESS", payload: result.data });
    });
  }, []);



  let settings = {
    className:"slideCard",
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: true,
    slidesToShow: 6,
    slidesToScroll: 1
  };



  let settings1 = {
    className:"slideCardS1",
    dots: true,
    infinite: true,
    speed: 500,
    autoplay: 1000,
    autoplaySpeed: 10000,
    slidesToShow: 1,
    cssEase: "ease-out",
    slidesToScroll: 1,
    afterChange: function (currentSlide) {
      setcurent(currentSlide)
     // console.log("after change", currentSlide);
      return currentSlide

    }
  };

  const imagesTest = [ 
   { img: "/images/Apple/apple5.jpg",title:"Apple Watch", par:"International Collection",id:1},
    {img: "/images/Apple/apple4.jpg", title:"iMac", par:"Say hello",id:2},
   { img: "/images/svg/nour1.jpg", title:"", par:"",id:3},
   { img: "/images/svg/Kitchen.jpg", title:"", par:"",id:4},
   { img: "/images/svg/Living_room_Wall_1.jpg", title:"", par:"",id:5},
    { img: "/images/svg/digital-laptop-working-global-business-concept.jpg", title: "", par: "", id:6}
  ]

  const imagesSlide = [ 
    { img: "/images/Apple/apple1.jpg",title:"iPhone 12", par:"International Collection"},
     {img: "/images/samsung/01.jpg", title:"Samsung", par:"Ultimate Collectios"},
    { img: "/images/svg/Untitled-1.jpg", title:"Huawei Mate", par:"life your Moment"}
  ]

  const images5 = [ 
    { img: "/images/samsung/galaxy2.jpg",title:"Apple Watch", par:"International Collection"},
    { img: "/images/samsung/bet room.jpg", title:"", par:""},
    { img: "/images/ikea/ein-bettgestell.jpg", title:"", par:""},
    { img: "/images/svg/dining-tabel1.jpg", title:"", par:""},
    { img:"/images/svg/white-livingroom.jpg", title:"", par:""}
  ]

  const content = [
    {
      title: "Vulputate Mollis Ultricies Fermentum Parturient",
      description:
        "Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum. Fusce dapibus, tellus ac cursus commodo, tortor mauris condimentum nibh, ut fermentum massa justo sit amet risus. Cras justo odio, dapibus ac facilisis.",
      button: "Read More",
      image: "/images/Apple/apple1.jpg",
      user: "Luan Gjokaj",
      userProfile: "https://i.imgur.com/JSW6mEk.png"
    },
    {
      title: "Tortor Dapibus Commodo Aenean Quam",
      description:
        "Nullam id dolor id nibh ultricies vehicula ut id elit. Cras mattis consectetur purus sit amet fermentum. Morbi leo risus, porta ac consectetur ac, vestibulum at eros. Donec sed odio dui.",
      button: "Discover",
      image: "/images/samsung/01.jpg",
      user: "Erich Behrens",
      userProfile: "https://i.imgur.com/0Clfnu7.png"
    },
    {
      title: "Phasellus volutpat metus",
      description:
        "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Cras mattis consectetur purus sit amet fermentum. Cras justo odio, dapibus ac facilisis in, egestas eget quam. Duis mollis, est non commodo luctus, nisi erat porttitor ligula.",
      button: "Buy now",
      image: "/images/svg/Untitled-1.jpg",
      user: "Bruno Vizovskyy",
      userProfile: "https://i.imgur.com/4KeKvtH.png"
    }
  ];

  return (
    <div className="container1">

      <Slider className="slider-wrapper" autoplay={4000} >
            {content.map((item, index) => (
               <div
               key={index}
               className="slider-content"
               style={{ background: `url('${item.image}') no-repeat center center` }}
             >
               <div className="inner">
                 <h1>{item.title}</h1>
                 <p>{item.description}</p>
                 <button>{item.button}</button>
               </div>
               <section>
                 <img src={item.userProfile} alt={item.user} />
                 <span>
                   Posted by <strong>{item.user}</strong>
                 </span>
               </section>
                 </div>    ))}
        </Slider>

        <div className="textCardViwer">
          <div>
             <h1>iPad Pro</h1>
            <p>Supercharged By Apple M1 chip</p>
          </div>
        <img width="100%" src="/images/Apple/apple3.jpg" alt=""/>
      </div>




      <div className="row">
         {imagesTest.map(el => <ImgTest el={el} />)}
        </div>

        <Slider {...settings}>  
     {products.map((event) => {
       return <Cards key={event._id} card={event} dispatch={dispatch} />;
            })
        }

      </Slider>

          <div className="sections row mt-3">   




        <div className="row cardRow">
          <div className="col">
          {imagesTest.map(el => <Card3D img={el.img} />)}

          </div>
        </div>




      </div>

<Footer/>     
    </div>
  );
}

export default HomeApi;
