 export const reducer = (oneItem, action) => {
    switch (action.type) {
        case 'FATCH_SUCCESS':
            return (
                 action.payload
            )
        case 'TOGGLE_WATCH':
            return {
                ...oneItem, completed: !action.payload.completed
            }
            case 'DELETE_ITEM':
                return (
                action.payload
                )
                case 'UPDATE_ITEM':
                    return (
                    action.payload
                )
                    case 'GETS_DATA':
                        return (
                        action.payload
                )
        default:
           return oneItem
    }
}
