import {React, useState,useContext} from 'react'
import axios from 'axios'
import { prodactsConext } from "../context/contextData";
import WatchList from '../watchList/WatchApi';

const Search = ({ open }) => {
const [searchData, setsearchData] = useState([])
    const [input, setInput] = useState('')
    const {
        dispatch,
        data,
      } = useContext(prodactsConext);

const handleChange = (e) => {
   setInput(e.target.value) 
}

    const searchApi = (e) => {

        axios.get("http://localhost:3001/products/search", {
            params: {
                query: input.trim()
            }
        }).then(response => {
            dispatch({ type: "SEARCH_DATA", payload: response.data })
            console.log(response)
            setsearchData(response.data)
            })
    }

    return (
        <div className={ open ? "search-form active" :"search-form"}>
        <form onSubmit={(e)=> e.preventDefault()} className="d-flex">
        <input onChange={handleChange} type="text" placeholder="Search"/>
        <button onClick={searchApi} class="btnSeatch" >Search</button>
            </form>
            <div className="sections row mt-3">
        <div className="row cardRow p-5">
          <div className="masonry">
            {searchData.map((el) =>  <WatchList el={el} />)}
          </div>
        </div>
      </div>
    </div>

    )
}

export default Search
