import Cards from "./cards";
import axios from "axios";
import {
  Route,
  Link
} from "react-router-dom";

function FetchingData() {

  const [data, setData] = useState([]);
  const [favBtn, setfavBtn] = useState(false)


useEffect(() => {
  getProduct();

},[])






useEffect(() => {

}, [favBtn])

const addToWachList = (card) => {
setfavBtn(!favBtn)
setData(data.map(el => {
if (el._id === card._id) {
    return {
        ...el, completed: !el.completed
    }
}
return el
}))
completesHandle(card)
}

const completesHandle = (card) => {
  axios.put(`http://localhost:3001/products/${card._id}`, card).then((result) => {
  console.log("result toggle", result.data);
  });
}












