import { React } from "react";
import { Link } from "react-router-dom";


const Cards = ({ card }) => {
  return (
    <>
      <div key={card._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: card.website ? "" : "red" }}
        >
          <Link to={`item/${card._id}`}>
            <div className="card-header">
              {card.img.map((e) => (
                <img
                  key={card._id}
                  style={{ maxWidth: "100%" }}
                  src={"http://localhost:3001/" + e}
                  alt={card.name}
                />
              ))}
            </div>
            <div className="card-body">
              <h2>{card.title}</h2>
              <h5>{card.category}</h5>
              <div>{card.price}</div>
            </div>
          </Link>
        </div>
      </div>
    </>
  );
};

export default Cards;
