import { React,useEffect } from "react";
import { Link } from "react-router-dom";
import { Parallax, ParallaxLayer, IParallax } from '@react-spring/parallax'
import styles from './styles.module.css'
const Cards = ({ card, dispatch,offset, gradient, onClick }) => {




  return (
    <>
       <ParallaxLayer offset={offset} speed={0.6} onClick={onClick}>
      <div className={`${styles.slopeEnd} ${styles[gradient]}`} />
    </ParallaxLayer>
        <div className="card cardV">
          <Link to={`item/${card._id}`}>
            <div className="card-header1">
              {card.img
                .map((e) => (
                  <img
                    key={card._id}
                    style={{ maxWidth: "100%" }}
                    src={"http://localhost:3001/" + e}
                    alt={card.name}
                  />
                ))
                .slice(0, 1)}
            </div>
            <div className="card-body">
              <h6>{card.title}</h6>
              <div>{card.category}</div>
              <span style={{"textDecoration": card.oldPrice ? "line-through" :''}}>{card.price} €</span>
             {card.oldPrice ? <span> {card.oldPrice} €</span> : ""}
            </div>
          </Link>
        </div>
    </>
  );
};

export default Cards;
