import { React, useContext, useEffect,useState } from "react";
import { prodactsConext } from "../context/contextData";
import Cards from "./Cards";
import { getsReq } from "../api/rest-helper";
import RecentList from '../recent/RecentList';
import DescountsList from '../discounts/DiscountsList'
import axios from "axios";

function HomeApi() {
  const { dispatch, products, user } = useContext(prodactsConext);

// Creat Fack User if there is no real User
    if (!user.user) {
     const _id = Date.now()
      dispatch({ type: "FACK_USER", payload:{ _id:_id} });
     }

  useEffect(() => {

      getsReq().then((result) => {
        dispatch({ type: "FATCH_SUCCESS", payload: result.data})
    })
  }, []);


  return (
    <div className="container">
      <div className="row">
      <div id="carouselExampleIndicators" class="carousel slide" data-ride="carousel">
  <ol class="carousel-indicators">
    <li data-target="#carouselExampleIndicators" data-slide-to="0" class="active"></li>
    <li data-target="#carouselExampleIndicators" data-slide-to="1"></li>
    <li data-target="#carouselExampleIndicators" data-slide-to="2"></li>
  </ol>
  <div class="carousel-inner">
    <div class="carousel-item active">
      <img class="d-block w-100" src="/images/Apple/apple1.jpg" alt="First slide"/>
    </div>
    <div class="carousel-item">
      <img class="d-block w-100" src="/images/samsung/01.jpg" alt="Second slide"/>
    </div>
    <div class="carousel-item">
      <img class="d-block w-100" src="/images/svg/Untitled-1.jpg" alt="Third slide"/>
    </div>
  </div>
  <a class="carousel-control-prev" href="#carouselExampleIndicators" role="button" data-slide="prev">
    <span class="carousel-control-prev-icon" aria-hidden="true"></span>
    <span class="sr-only">Previous</span>
  </a>
  <a class="carousel-control-next" href="#carouselExampleIndicators" role="button" data-slide="next">
    <span class="carousel-control-next-icon" aria-hidden="true"></span>
    <span class="sr-only">Next</span>
  </a>
</div>

        {products.length ? products.map((event) => {
          return (
            <Cards
              key={event._id}
              card={event}
              dispatch={dispatch}
            />
          );
        }) : 'loading'}
        <RecentList />
        <DescountsList/>
      </div>
    </div>
  );
}

export default HomeApi;
