import { React, useState } from "react";
import axios from "axios";
import {getReq, putReq, deleteReq} from './useFetch'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
    useRouteMatch,
    Redirect,
  useParams,
} from "react-router-dom";

const Edit = ({ card, setEdit, dispatch, setData }) => {
  const [input, setInput] = useState(false);
    const [gitOneData, setGitOneData] = useState(false);
    const [redirect, setredirect] = useState(false)

  const { id } = useParams();

  if (!gitOneData) {
      setGitOneData(true);
      getReq(id).then(result => setInput(result))
  }

  const handleChange = (e) =>
    setInput({ ...input, [e.target.name]: e.target.value });

  const handleUpdate = (e) => {
    e.preventDefault();
    putReq(input, id).then(result => dispatch({ type: 'DELETE_ITEM', payload: result.data })  )
      setEdit(false);

  };

  const handleDelete = (e) => {
    e.preventDefault();
      deleteReq(id).then(result => dispatch({ type: 'DELETE_ITEM', payload: result.data }));
      setredirect(true)
  };


  return (
      <>
          {redirect ? <Redirect to="/" /> :''}
      <div key={card._id} className="col-md-4 col-sm-6">
        <div
          className="card"
          style={{ borderColor: card.website ? "" : "red" }}
        >
          <div className="card-header">
            <img style={{ maxWidth: "100%" }} src={card.img} alt={card.name} />
          </div>
          <div className="card-body">
            <input
              placeholder="update your data"
              value={input.title}
              onChange={handleChange}
              name="title"
              // ref={inputRef}
              className="todo-input edit"
            />
            <input
              placeholder="update your data"
              value={input.category}
              onChange={handleChange}
              name="category"
              // ref={inputRef}
              className="todo-input edit"
            />

            <input
              placeholder="update your data"
              value={input.price}
              onChange={handleChange}
              name="price"
              // ref={inputRef}
              className="todo-input edit"
            />
            <div></div>
            <div></div>
          </div>
        </div>
        <button className="btn btn-warning" onClick={handleUpdate}>
          Update
        </button>
        <button className="btn btn-danger" onClick={handleDelete}>
          delete
        </button>
      </div>
    </>
  );
};

export default Edit;
