  console.log("img",img)
  return (
    <Tilt className="track3D"  glareEnable={false} glareMaxOpacity={0.8} glareColor="#ffffff" glarePosition="bottom" glareBorderRadius="20px">
             <div className="base-demo">
      <ImageShadow shadowHover src={"http://localhost:3001/" + img}></ImageShadow>
    </div>

    </Tilt>
  );
}

export default Card3D;
