import { React,useEffect, useReducer } from "react";
import { basketReducer } from "../reducers/reducer";
import WatchList from "./BasketList";
import { getsReq } from "../api/rest-helper";

const WatchApi = () => {
  const [oneItem, dispatch] = useReducer(basketReducer, []);


  return (
    <div>
      <div className="container">
        <div className="row">
          <h1 className="text-center">Basket</h1>
          {oneItem.map((el) => {
            return <WatchList key={el._id} oneItem={el} dispatch={ dispatch}/>;
          })}
        </div>
      </div>
    </div>
  );
};

export default WatchApi;
